import React from "react";
import { Link } from "react-router-dom";
import CarUIMoveable from '../components/CarUIMoveable'; //'./' is current folder
import Moveable from "react-moveable";

const carUIPage = () => {
    const [target, setTarget] = React.useState();
    const [frame, setFrame] = React.useState({
        translate: [0,0],
        rotate: 0,
        transformOrigin: "50% 50%",
    });
    React.useEffect(() => {
        setTarget(document.querySelector(".target"));
    }, []);
    return (
        <div>
        <div className="target">Target</div>
    <Moveable
        target={target}
        originDraggable={true}
        originRelative={true}
        draggable={true}
        throttleDrag={0}
        startDragRotate={0}
        throttleDragRotate={0}
        zoom={1}
        origin={true}
        padding={{"left":0,"top":0,"right":0,"bottom":0}}
        rotatable={true}
        throttleRotate={0}
        rotationPosition={"top"}
        onDragOriginStart={({ dragStart }) => {
            dragStart && dragStart.set(frame.translate);
        }}
        onDragOrigin={({ target, drag, transformOrigin }) => {
            frame.translate = drag.beforeTranslate;
            frame.transformOrigin = transformOrigin;
        }}
        onDragStart={({ set }) => {
            set(frame.translate);
        }}
        onDrag={({ target, beforeTranslate }) => {
            frame.translate = beforeTranslate;
        }}
        onRotateStart={({ set }) => {
            set(frame.rotate);
        }}
        onRotate={({ beforeRotate }) => {
            frame.rotate = beforeRotate;
        }}
        onRender={({ target }) => {
            const { translate, rotate, transformOrigin } = frame;
            target.style.transformOrigin = transformOrigin;
            target.style.transform = `translate(${translate[0]}px, ${translate[1]}px)`
                +  ` rotate(${rotate}deg)`;
        }}
    />
            <CarUIMoveable moveableTarget="target" />
            <div class="target"> Target
            </div>
            <img class="target" src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/Cutest_Koala.jpg/1117px-Cutest_Koala.jpg"/>
            <h1>Car UI Page</h1>
            <Link to="/upload_img">Upload Image</Link>
            <Link to="/edit_img">Edit Existing Image</Link>
            <h2>Edit Existing Image</h2>
            <ul>
                <li>Size, position, rotation</li>
                <li>Delete</li>
                <li>Replace</li>
            </ul>
            <Link to="/">Back to Profile Page</Link>
        </div>
    );
};

export default carUIPage;
