import React, {Component} from 'react';
import Moveable from "react-moveable";


export default class CarUIMoveable extends Component { //<h1>{moveableTarget}</h1>;
  constructor(props){
    super(props);
    this.state = {
      target: null,
      frame: {
        translate: [0,0],
        rotate: 0,
        transformOrigin: "50% 50%"
      }
    }
  }

  componentDidMount(){
    this.setState({
      target: document.querySelector(".target"),
      frame: {
        translate: [0,0],
        rotate: 0,
        transformOrigin: "50% 50%"
      }
    });
  }

  render() {
    return <div className="container">
        <div className="target">Target</div>
        <Moveable
            target={this.state.target}
            originDraggable={true}
            originRelative={true}
            draggable={true}
            throttleDrag={0}
            startDragRotate={0}
            throttleDragRotate={0}
            zoom={1}
            origin={true}
            padding={{"left":0,"top":0,"right":0,"bottom":0}}
            rotatable={true}
            throttleRotate={0}
            rotationPosition={"top"}
            onDragOriginStart={({ dragStart }) => {
                dragStart && dragStart.set(frame.translate);
            }}
            onDragOrigin={({ target, drag, transformOrigin }) => {
                frame.translate = drag.beforeTranslate;
                frame.transformOrigin = transformOrigin;
            }}
            onDragStart={({ set }) => {
                set(frame.translate);
            }}
            onDrag={({ target, beforeTranslate }) => {
                frame.translate = beforeTranslate;
            }}
            onRotateStart={({ set }) => {
                set(frame.rotate);
            }}
            onRotate={({ beforeRotate }) => {
                frame.rotate = beforeRotate;
            }}
            onRender={({ target }) => {
                const { translate, rotate, transformOrigin } = frame;
                target.style.transformOrigin = transformOrigin;
                target.style.transform = `translate(${translate[0]}px, ${translate[1]}px)`
                    +  ` rotate(${rotate}deg)`;
            }}
        />
    </div>;
}
}
