import React, {Component} from "react";
import ReactDOM from 'react-dom'

import { Link } from "react-router-dom";
import CarUIMoveable from '../components/CarUIMoveable'; //'./' is current folder

// var value = "target"
//
// var element = <CarUIMoveable id = "carUIMoveable" moveableTarget={value} />;
//
// function changeClass(){
//   if (value == "target")
//     value = "temp"
//   else
//     value = "target"
//   element.resetState;
// }


export default class carUIPage extends Component {
  constructor(props){
    super(props);
    this.changeTarget = this.changeTarget.bind(this);
    this.callResetStates = this.callResetStates.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.finishClick = this.finishClick.bind(this);


    this.changeChild = React.createRef();
    this.begin = null;
    this.end = null;
  }



  // changeClass(){
  //   this.reloadChild();
  //   console.log("hi")
  //   if (this.value == "target")
  //       this.value = "temp"
  //     else
  //       this.value = "target"
  // }
  changeTarget(newTarget, delay = 0) {
    if (newTarget == null || newTarget == ""){
      newTarget ="none"
    }
    if (this.changeChild.current == null) {
      console.log('null')

    }
    else {
      console.log('resettingstates')
      setTimeout(this.callResetStates(newTarget), delay);

      // this.changeChild.current.resetStates(newTarget);

    }
    this.value = "target"
  }

  callResetStates(newTarget) {
    this.changeChild.current.resetStates(newTarget);
  }

  componentDidMount() {
    console.log(this.changeChild.current)
    // this.setState({ bodyHeight: this.calendarBodyRef .current.clientHeight });
  }



  handleClick(e){
    console.log("mouse is down!")
    this.begin = new Date();
  }

  finishClick(e){
    console.log("mouse is up!")
    this.end = new Date();
    if ((this.end - this.begin) < 100) {
      if (e.target.className.startsWith('moveable'))
      this.changeTarget(e.target.className)
    }
  }


  render() {
    return (
        <div onMouseDown = {this.handleClick} onClick = {this.finishClick}>
        <div></div>
            <CarUIMoveable id = "carUIMoveable" ref = {this.changeChild} moveableTarget="target" />
            <button onClick = {console.log('hi')}>click </button>
            <img className="moveable koala1" src="https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/Cutest_Koala.jpg/1117px-Cutest_Koala.jpg"/>
            <h1>Car UI Page</h1>
            <Link to="/upload_img">Upload Image</Link>
            <Link to="/edit_img">Edit Existing Image</Link>
            <h2>Edit Existing Image</h2>
            <ul>
                <li>Size, position, rotation</li>
                <li>Delete</li>
                <li>Replace</li>
            </ul>
            <Link to="/">Back to Profile Page</Link>
        </div>
    );
};
}
