import React, {Component} from 'react';
import Moveable from "moveable";


class CarUIMoveable  extends Component { //<h1>{moveableTarget}</h1>;

  componentDidMount(){
  const moveable = new Moveable(document.body, {
      // If you want to use a group, set multiple targets(type: Array<HTMLElement | SVGElement>).
      target: document.querySelector(".target"),
      originDraggable: true,
      originRelative: true,
      draggable: true,
      throttleDrag: 0,
      startDragRotate: 0,
      throttleDragRotate: 0,
      zoom: 1,
      origin: true,
      padding: {"left":0,"top":0,"right":0,"bottom":0},
      rotatable: true,
      throttleRotate: 0,
      rotationPosition: "top",
  });
  let frame = {
      translate: [0,0],
      rotate: 0,
      transformOrigin: "50% 50%",
  };
  moveable.on("dragOriginStart", ({ dragStart }) => {
      dragStart && dragStart.set(frame.translate);
  }).on("dragOrigin", ({ target, drag, transformOrigin }) => {
      frame.translate = drag.beforeTranslate;
      frame.transformOrigin = transformOrigin;
  }).on("dragStart", ({ set }) => {
      set(frame.translate);
  }).on("drag", ({ target, beforeTranslate }) => {
      frame.translate = beforeTranslate;
  }).on("rotateStart", ({ set }) => {
      set(frame.rotate);
  }).on("rotate", ({ beforeRotate }) => {
      frame.rotate = beforeRotate;
  }).on("render", ({ target }) => {
      const { translate, rotate, transformOrigin } = frame;
      target.style.transformOrigin = transformOrigin;
      target.style.transform = `translate(${translate[0]}px, ${translate[1]}px)`
          +  ` rotate(${rotate}deg)`;
  });

  const mystyle = {
        background: "url('https://cdn.britannica.com/26/162626-050-3534626F/Koala.jpg')",
        backgroundRepeat: "no-repeat",
        backgroundSize: "auto",
        height: "1600px",
        width: "1279px;"
      };



  return (
      <div>
        <h1>I AM HERE</h1>
          <div class="target" style = {mystyle}></div>
      </div>
    );
}
}
export default CarUIMoveable;
