//Logs in an existing user and gives option to register new user

import React from 'react';
import { connect } from 'react-redux';
import { Form, Button } from 'react-bootstrap';
import { initiateLogin } from '../actions/auth';
import { validateFields } from '../utils/common';
import { Link } from 'react-router-dom';

class Login extends React.Component {
    state = {
        profile_name: '',
        password: '',
        errorMsg: ''
};

handleLogin = (event) => {
    event.preventDefault();
    const {profile_name, password} = this.state;
    const fieldsToValidate = [{ profile_name }, { password }];
    const allFieldsEntered = validateFields(fieldsToValidate);

    if (!allFieldsEntered) 
    {
      this.setState({errorMsg: { signin_error: 'Please enter all the fields.' }});
    } 
    //Login successful
    else 
    {
      this.setState({ errorMsg: { signin_error: '' }}); 
    }
};

handleInputChange = (event) => {
    const { profile_name, value } = event.target;
    this.setState({
        [profile_name]: value
    });
};

render() {
    const { errorMsg } = this.state;
    return (
      <div className="login-page">
        <h1>Login or Create Create an Account!</h1>
        <div className="login-form">
          <Form onSubmit={this.handleLogin}>
            {errorMsg && errorMsg.signin_error && (
              <p className="errorMsg centered-message">
                {errorMsg.signin_error}
              </p>
            )}
            <Form.Group controlId="profile_name">
              <Form.Label>Profile Name</Form.Label>
              <Form.Control
                type="text"
                name="profile_name"
                placeholder="Enter Profile Name"
                onChange={this.handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                placeholder="Enter password"
                onChange={this.handleInputChange}
              />
            </Form.Group>
            <div className="action-items">
              <Button variant="primary" type="submit">
                Login
              </Button>
              <Link to="/register_user" className="btn btn-secondary">
                Create account
              </Link>
            </div>
          </Form>
        </div>
      </div>
    );
  }
}

export default connect()(Login);
