        const {
            throttleRotate = 0,
            parentMoveable,
        } = moveable.props;

        let delta: number;
        let dist: number;
        let rotate: number;
        let beforeDelta: number;
        let beforeDist: number;
        let beforeRotate: number;

        const startDeg = 180 / Math.PI * startValue;

        if (!parentFlag && "parentDist" in e) {
