import React, {Component} from 'react';
import Moveable from "react-moveable";


export default class CarUIMoveable extends Component { //<h1>{moveableTarget}</h1>;
  constructor(props){
    super(props);
    this.state = {
      target: null,
      frame: {
        translate: [0,0],
        rotate: 0,
        scale: [1,1],
        transformOrigin: "50% 50%"
      }
    }
  }

  componentDidMount(){
    this.setState({
      target: document.querySelector(".target"),
      frame: {
        translate: [0,0],
        scale: [1,1],
        rotate: 0,
        transformOrigin: "50% 50%"
      }
    });
  }

  render() {
    // put 1 below:  <div className="target">Target</div>

    return <div className="container">
        <Moveable
            target={this.state.target}

            //Scaleable
            scalable={true}
            keepRatio={false}
            throttleScale={0}
            renderDirections={["nw","n","ne","w","e","sw","s","se"]}
            edge={false}
            zoom={1}
            origin={true}
            padding={{"left":0,"top":0,"right":0,"bottom":0}}
            onScaleStart={({ set, dragStart }) => {
                set(frame.scale);
                dragStart && dragStart.set(frame.translate);
            }}
            onScale={({ target, scale, drag }) => {
                const beforeTranslate = drag.beforeTranslate;

                frame.translate = beforeTranslate;
                frame.scale = scale;
                target.style.transform
                    = `translate(${beforeTranslate[0]}px, ${beforeTranslate[1]}px)`
                    + ` scale(${scale[0]}, ${scale[1]})`;
            }}
            onScaleEnd={({ lastEvent }) => {
                if (lastEvent) {
                    frame.translate = lastEvent.drag.beforeTranslate;
                    frame.scale = lastEvent.scale;
                }
            }}
        />
    </div>;
}
}
